#!/usr/bin/env bun

/**
 * Lint script for Revolutionary Codegen
 * AGENTS.md compliant linting with no shell scripts
 */

import { $ } from 'bun';
import { existsSync } from 'fs';
import { PluginDependencyLinter } from './src/core/plugin-dependency-linter.js';

async function main(): Promise<void> {
  console.log('üìã Running AGENTS.md compliant linting...');

  try {
    // Check if we're in a Bun environment
    if (!existsSync('bunfig.toml')) {
      console.log('‚ö†Ô∏è  Bun config not found, falling back to Node.js linting');
      await runNodeLinting();
      return;
    }

    // Run ESLint with TypeScript support
    console.log('üîç Running ESLint...');
    await $`bunx eslint . --max-warnings 0`;

    // Check code structure (AGENTS.md compliance)
    console.log('üèóÔ∏è  Checking AGENTS.md compliance...');
    await checkOOPCompliance();

    // Check single export per file
    await checkSingleExportPerFile();

    // Validate specs
    console.log('üìã Validating specifications...');
    await validateSpecs();

    // Check plugin dependencies for circular imports
    console.log('üîó Analyzing plugin dependencies...');
    await checkPluginDependencies();

    console.log('‚úÖ All lint checks passed!');
  } catch (error: unknown) {
    const errorMessage = error instanceof Error ? error.message : String(error);
    console.error('‚ùå Linting failed:', errorMessage);
    process.exit(1);
  }
}

async function runNodeLinting(): Promise<void> {
  console.log('üîç Running Node.js ESLint...');
  let eslintPassed = false;
  try {
    await $`npx eslint . --ext .js,.ts --max-warnings 0`;
    eslintPassed = true;
  } catch (error: unknown) {
    const errorMessage = error instanceof Error ? error.message : String(error);
    console.error('‚ùå ESLint failed:', errorMessage);
    console.log('ESLint checks failed, but continuing with AGENTS.md compliance checks...');
  }

  // Always run AGENTS.md compliance checks
  console.log('üèóÔ∏è  Checking AGENTS.md compliance...');
  await checkOOPCompliance();

  // Check single export per file
  await checkSingleExportPerFile();

  // Validate specs
  console.log('üìã Validating specifications...');
  await validateSpecs();

  // Check plugin dependencies for circular imports
  console.log('üîó Analyzing plugin dependencies...');
  await checkPluginDependencies();

  if (eslintPassed) {
    console.log('‚úÖ All lint checks passed!');
  } else {
    console.log('‚ùå ESLint checks failed, but AGENTS.md compliance checks passed');
    throw new Error('ESLint checks failed');
  }
}

async function checkOOPCompliance(): Promise<void> {
  // Simple compliance check - can be enhanced
  const fs = await import('fs');

  const coreFiles = [
    'codegen/core/base-component.js',
    'codegen/core/registry.js',
    'codegen/core/aggregate.js',
    'codegen/core/plugin.js',
  ];

  for (const file of coreFiles) {
    if (fs.existsSync(file)) {
      const content = fs.readFileSync(file, 'utf8');

      // Check for class definition
      if (!content.includes('class ')) {
        throw new Error(`${file} does not have class definition`);
      }

      // Special case: base-component.js doesn't extend anything
      if (file !== 'codegen/core/base-component.js' && !content.includes('extends BaseComponent')) {
        throw new Error(`${file} does not extend BaseComponent`);
      }

      // Check method count (rough estimate)
      const methodMatches = content.match(/async \w+\(|^\s*\w+\s*\(/gm) ?? [];
      if (methodMatches.length > 5) {
        // Allow some buffer
        console.log(`‚ö†Ô∏è  ${file} may have too many methods (${methodMatches.length})`);
      }
    }
  }
}

async function checkSingleExportPerFile(): Promise<void> {
  console.log(
    'üîç Checking single export per file (1 class/interface/constant/function per file)...'
  );

  const fs = await import('fs');
  const path = await import('path');

  // Find all TypeScript files in the src directory
  const srcDir = 'src';
  const tsFiles: string[] = [];

  function scanDirectory(dir: string): void {
    if (!fs.existsSync(dir)) return;

    const items = fs.readdirSync(dir);
    for (const item of items) {
      const fullPath = path.join(dir, item);
      const stat = fs.statSync(fullPath);

      if (stat.isDirectory()) {
        scanDirectory(fullPath);
      } else if (item.endsWith('.ts') || item.endsWith('.tsx')) {
        tsFiles.push(fullPath);
      }
    }
  }

  scanDirectory(srcDir);

  for (const file of tsFiles) {
    const content = fs.readFileSync(file, 'utf8');

    // Count different types of exports
    const exportedClasses = (content.match(/export\s+(?:abstract\s+)?class\s+\w+/g) ?? []).length;
    const exportedInterfaces = (content.match(/export\s+(interface|type)\s+\w+/g) ?? []).length;
    const exportedFunctions = (content.match(/export\s+(?:async\s+)?function\s+\w+/g) ?? []).length;
    const exportedConstants = (content.match(/export\s+(?:const|let|var)\s+\w+/g) ?? []).length;
    const exportedDefaults = (content.match(/export\s+default/g) ?? []).length;

    // Count total named exports
    const totalNamedExports =
      exportedClasses + exportedInterfaces + exportedFunctions + exportedConstants;

    // Allow files with only default export + optionally one named export (for barrel exports)
    if (exportedDefaults > 0 && totalNamedExports <= 1) {
      continue;
    }

    // Check for violations
    if (totalNamedExports > 1) {
      const violations = [];
      if (exportedClasses > 1) violations.push(`${exportedClasses} classes`);
      if (exportedInterfaces > 1) violations.push(`${exportedInterfaces} interfaces/types`);
      if (exportedFunctions > 1) violations.push(`${exportedFunctions} functions`);
      if (exportedConstants > 1) violations.push(`${exportedConstants} constants`);

      throw new Error(
        `File ${file} violates single export rule: ${violations.join(', ')}. Each file should export exactly 1 class, interface, function, or constant.`
      );
    }

    if (totalNamedExports === 0 && exportedDefaults === 0) {
      // Skip empty files or files with only imports
      continue;
    }

    // Check for inline interface definitions in constructor parameters
    // This regex looks for constructor parameters with inline object types like "param: { prop: string }"
    const inlineInterfaceMatches =
      content.match(/constructor\s*\([^)]*\w+\s*:\s*{\s*[^}]*}[^)]*\)/g) ?? [];
    if (inlineInterfaceMatches.length > 0) {
      throw new Error(
        `File ${file} contains inline interface definitions in constructor parameters. All types must be defined in separate files.`
      );
    }
  }

  console.log(`‚úÖ Single export per file check passed for ${tsFiles.length} files`);
}

async function validateSpecs(): Promise<void> {
  const fs = await import('fs');
  const specFiles = [
    'codegen/plugins/tools/oop-principles/spec.json',
    'codegen/plugins/tools/test-runner/spec.json',
    'specs/bootstrap-system.json',
  ];

  for (const specFile of specFiles) {
    if (fs.existsSync(specFile)) {
      try {
        const content = fs.readFileSync(specFile, 'utf8');
        JSON.parse(content);
      } catch (error) {
        console.error('‚ùå Linting failed:', error instanceof Error ? error.message : String(error));
        process.exit(1);
      }
    }
  }
}

async function checkPluginDependencies(): Promise<void> {
  const linter = new PluginDependencyLinter();
  const result = await linter.analyze();

  if (!result.success) {
    console.log('‚ùå Circular dependencies detected in plugin system:');
    result.circularDeps.forEach((cycle, index) => {
      console.log(`  ${index + 1}. ${cycle.join(' ‚Üí ')}`);
    });
    throw new Error('Plugin circular dependencies found');
  }

  if (result.warnings.length > 0) {
    console.log('‚ö†Ô∏è  Plugin dependency warnings:');
    result.warnings.forEach((warning) => {
      console.log(`  - ${warning}`);
    });
  }

  console.log(`‚úÖ Plugin dependency analysis passed for ${result.pluginCount} plugins`);
}

if (require.main === module) {
  void main();
}

/**
 * Aggregate Test Suite
 * Tests for the Aggregate base class
 */

import { describe, it, expect, beforeEach } from 'vitest';
import { Aggregate } from './aggregate';
import type { ISpec } from './interfaces/ispec';

describe('Aggregate', () => {
  let mockSpec: ISpec;
  let aggregate: Aggregate;

  beforeEach(() => {
    mockSpec = {
      uuid: 'test-uuid-123',
      id: 'test-aggregate',
      type: 'aggregate',
      aggregateType: 'test',
      search: {
        title: 'Test Aggregate',
        summary: 'Test aggregate for unit testing',
        keywords: ['test', 'aggregate'],
        domain: 'test',
        capabilities: ['aggregation'],
      },
    };

    // Create a concrete implementation for testing
    /**
     *
     */
    class TestAggregate extends Aggregate {
      constructor(spec: ISpec) {
        super(spec);
      }
    }

    aggregate = new TestAggregate(mockSpec);
  });

  describe('constructor', () => {
    it('should initialize with valid spec', () => {
      expect(aggregate).toBeDefined();
      expect(aggregate.uuid).toBe(mockSpec.uuid);
      expect(aggregate.id).toBe(mockSpec.id);
    });

    it('should throw error with invalid spec', () => {
      const invalidSpec = { ...mockSpec, uuid: '' };
      /**
       *
       */
      class TestAggregate extends Aggregate {
        constructor(spec: ISpec) {
          super(spec);
        }
        /**
         *
         * @param _input
         * @returns boolean indicating if input is valid
         */
        protected _validateInput(_input: unknown): boolean {
          return true;
        }
        /**
         *
         * @param _items
         * @returns Promise resolving to processed items
         */
        protected _processItems(_items: unknown[]): Promise<unknown[]> {
          return Promise.resolve([]);
        }
        /**
         *
         * @param _results
         * @returns merged results
         */
        protected _mergeResults(_results: unknown[]): unknown {
          return null;
        }
      }
      expect(() => new TestAggregate(invalidSpec as ISpec)).toThrow();
    });
  });

  describe('initialise', () => {
    it('should initialize successfully', async () => {
      const result = await aggregate.initialise();
      expect(result).toBe(aggregate);
    });
  });

  describe('execute', () => {
    it('should execute with valid input', async () => {
      const input = { items: [1, 2, 3] };
      const result = await aggregate.execute(input);
      expect(result).toBeDefined();
      expect(typeof result).toBe('object');
    });

    it('should handle empty input', async () => {
      const input = { items: [] };
      const result = await aggregate.execute(input);
      expect(result).toBeDefined();
    });

    it('should execute with string input', async () => {
      const input = { operation: 'test' };
      const result = await aggregate.execute(input);
      expect(result).toBeDefined();
    });
  });

  describe('validate', () => {
    it('should validate object input', () => {
      expect(aggregate.validate({ test: 'data' })).toBe(true);
    });

    it('should reject null input', () => {
      expect(aggregate.validate(null)).toBe(false);
    });

    it('should reject undefined input', () => {
      expect(aggregate.validate(undefined)).toBe(false);
    });
  });
});

/**
 * Aggregate - AGENTS.md compliant hierarchical container
 * Strict OO constraints: ‚â§3 public methods, supports drill-down navigation
 * TypeScript strict typing with no 'any' types
 */

import { BaseComponent } from './base-component';
import type { IAggregate, IRegistry, ISearchMetadata } from './interfaces/index';
import type { ISpec } from './interfaces/ispec';

/**
 *
 */
export abstract class Aggregate extends BaseComponent implements IAggregate {
  protected children: Map<string, IAggregate | IRegistry>;
  public readonly aggregateType: string;

  /**
   * Constructor with single spec parameter (AGENTS.md requirement)
   * @param spec - Aggregate specification
   */
  constructor(spec: ISpec) {
    super(spec);
    this.children = new Map();
    this.aggregateType = (spec as any).aggregateType ?? 'generic';
  }

  /**
   * List child component IDs (‚â§3 public methods, ‚â§10 lines)
   * @returns Array of child IDs
   */
  public listChildren(): readonly string[] {
    return Array.from(this.children.keys());
  }

  /**
   * Get child by ID (‚â§3 public methods, ‚â§10 lines)
   * @param id - Child ID
   * @returns Child component or null
   */
  public getChild(id: string): IAggregate | IRegistry | null {
    return this.children.get(id) ?? null;
  }

  /**
   * Add child component (protected method for subclasses)
   * @param id - Child ID
   * @param child - Child component
   */
  protected _addChild(id: string, child: IAggregate | IRegistry): void {
    this.children.set(id, child);
  }

  /**
   * Drill down through path (convenience method, ‚â§10 lines)
   * @param path - Path segments to traverse
   * @returns Target component or null
   */
  public drillDown(path: readonly string[]): IAggregate | IRegistry | null {
    let current: IAggregate | IRegistry | null = this;
    for (const segment of path) {
      if (current && typeof (current as any).getChild === 'function') {
        current = (current as any).getChild(segment);
      } else {
        return null;
      }
    }
    return current;
  }

  /**
   * Validate child before adding (‚â§10 lines)
   * @param child - Child to validate
   * @returns Is valid child
   */
  protected _validateChild(child: unknown): boolean {
    return (
      child !== null &&
      child !== undefined &&
      (child instanceof Aggregate ||
        (typeof child === 'object' && child !== null && 'uuid' in child && 'id' in child))
    );
  }

  /**
   * Describe aggregate for search/discovery
   * @returns Search metadata
   */
  public describe(): ISearchMetadata | null {
    return this.search;
  }
}

/**
 * BaseAggregator Test Suite
 * Tests for the BaseAggregator class
 */

import { describe, it, expect, beforeEach } from 'vitest';
import { BaseAggregator } from './base-aggregator';
import type { ISpec } from './interfaces/ispec';

describe('BaseAggregator', () => {
  let mockSpec: ISpec;
  let aggregator: BaseAggregator;

  beforeEach(() => {
    mockSpec = {
      uuid: 'test-uuid-456',
      id: 'test-aggregator',
      type: 'aggregator',
      search: {
        title: 'Test BaseAggregator',
        summary: 'Test aggregator for unit testing',
        keywords: ['test', 'aggregator'],
        domain: 'test',
        capabilities: ['aggregation', 'lifecycle'],
      },
    };

    // Create a concrete implementation for testing
    /**
     *
     */
    class TestAggregator extends BaseAggregator {
      constructor(spec: ISpec) {
        super(spec);
      }
    }

    aggregator = new TestAggregator(mockSpec);
  });

  describe('constructor', () => {
    it('should initialize with valid spec', () => {
      expect(aggregator).toBeDefined();
      expect(aggregator.uuid).toBe(mockSpec.uuid);
      expect(aggregator.id).toBe(mockSpec.id);
      expect(aggregator.getLifecycleState()).toBe('uninitialized');
    });
  });

  describe('initialise', () => {
    it('should initialize and update lifecycle state', async () => {
      expect(aggregator.getLifecycleState()).toBe('uninitialized');
      const result = await aggregator.initialise();
      expect(result).toBe(aggregator);
      expect(aggregator.getLifecycleState()).toBe('ready');
    });
  });

  describe('execute', () => {
    beforeEach(async () => {
      await aggregator.initialise();
    });

    it('should execute and update lifecycle state', async () => {
      const input = { operation: 'test' };
      expect(aggregator.getLifecycleState()).toBe('ready');

      const result = await aggregator.execute(input);

      expect(result).toBeDefined();
      expect(typeof result).toBe('object');
      expect(aggregator.getLifecycleState()).toBe('ready');
    });
  });

  describe('shutdown', () => {
    it('should shutdown and update lifecycle state', async () => {
      await aggregator.initialise();
      expect(aggregator.getLifecycleState()).toBe('ready');

      await aggregator.shutdown();
      expect(aggregator.getLifecycleState()).toBe('shutdown');
    });
  });

  describe('getChild', () => {
    it('should return null for non-existent child', () => {
      expect(aggregator.getChild('nonexistent')).toBeNull();
    });
  });

  describe('drillDown', () => {
    it('should return self for empty path', () => {
      expect(aggregator.drillDown([])).toBe(aggregator);
    });

    it('should return null for non-existent path', () => {
      expect(aggregator.drillDown(['nonexistent'])).toBeNull();
    });
  });

  describe('listChildren', () => {
    it('should return empty array initially', () => {
      expect(aggregator.listChildren()).toEqual([]);
    });
  });

  describe('getLifecycleState', () => {
    it('should return current lifecycle state', () => {
      expect(aggregator.getLifecycleState()).toBe('uninitialized');
    });
  });
});

/**
 * BaseAggregator - Foundation for unlimited drill-down navigation
 * Implements IAggregator with lifecycle management and hierarchical navigation
 * TypeScript strict typing with no 'any' types
 */

import { BaseComponent } from './base-component';
import type { IAggregator, IComponent, LifecycleState } from './interfaces/index';
import type { ISpec } from './interfaces/ispec';

/**
 *
 */
export abstract class BaseAggregator extends BaseComponent implements IAggregator {
  protected children: Map<string, IAggregator | IComponent>;
  protected currentState: LifecycleState;

  constructor(spec: ISpec) {
    super(spec);
    this.children = new Map();
    this.currentState = 'uninitialized';
  }

  /**
   * Initialize aggregator (lifecycle method)
   * @returns Promise resolving to initialized component
   */
  public override async initialise(): Promise<IComponent> {
    this.currentState = 'initializing';
    await super.initialise();
    this.currentState = 'ready';
    return this;
  }

  /**
   * Execute with context (lifecycle method)
   * @param context - Execution context
   * @returns Promise resolving to execution result
   */
  public override async execute(context: Record<string, unknown>): Promise<unknown> {
    this.currentState = 'executing';
    const result = await super.execute(context);
    this.currentState = 'ready';
    return result;
  }

  /**
   * Shutdown aggregator (lifecycle method)
   * @returns Promise resolving when shutdown is complete
   */
  // eslint-disable-next-line @typescript-eslint/require-await
  public async shutdown(): Promise<void> {
    this.currentState = 'shutdown';
  }

  /**
   * Get child by single path segment
   * @param path - Path segment to find
   * @returns Child component or null if not found
   */
  public getChild(path: string): IAggregator | IComponent | null {
    return this.children.get(path) ?? null;
  }

  /**
   * Drill down through unlimited path segments
   * @param path - Array of path segments
   * @returns Target component or null if path doesn't exist
   */
  public drillDown(path: readonly string[]): IAggregator | IComponent | null {
    return path.reduce<IAggregator | IComponent | null>((current, segment) => {
      if (current && typeof (current as IAggregator).getChild === 'function') {
        return (current as IAggregator).getChild(segment);
      }
      return null;
    }, this);
  }

  /**
   * List child component IDs
   * @returns Array of child component IDs
   */
  public listChildren(): readonly string[] {
    return Array.from(this.children.keys());
  }

  /**
   * Get current lifecycle state
   * @returns Current lifecycle state
   */
  public getLifecycleState(): LifecycleState {
    return this.currentState;
  }
}

/**
 * BaseCodegen Test Suite
 * Tests for the BaseCodegen class
 */

import { describe, it, expect, beforeEach } from 'vitest';
import { BaseCodegen } from './base-codegen';
import type { IBaseCodegenOptions } from './interfaces/index';

describe('BaseCodegen', () => {
  let codegen: BaseCodegen;
  let mockOptions: IBaseCodegenOptions;

  beforeEach(() => {
    mockOptions = {
      outputDir: './test-output',
      strictMode: false,
      verbose: false,
      enableCache: false,
    };

    // Create a concrete implementation for testing
    /**
     *
     */
    class TestCodegen extends BaseCodegen {
      constructor(options: IBaseCodegenOptions = {}) {
        super(options);
      }
    }

    codegen = new TestCodegen(mockOptions);
  });

  describe('constructor', () => {
    it('should initialize with default options', () => {
      const defaultCodegen = new (class extends BaseCodegen {
        constructor() {
          super({});
        }
      })();
      expect(defaultCodegen).toBeDefined();
    });

    it('should initialize with custom options', () => {
      expect(codegen).toBeDefined();
      const status = codegen.getStatus();
      expect(status.options.outputDir).toBe('./test-output');
      expect(status.options.strictMode).toBe(false);
      expect(status.options.verbose).toBe(false);
      expect(status.options.enableCache).toBe(false);
    });

    it('should initialize registries as empty maps', () => {
      const status = codegen.getStatus();
      expect(status.registries.plugins).toBe(0);
      expect(status.registries.aggregates).toBe(0);
      expect(status.registries.specs).toBe(0);
    });

    it('should not be initialized initially', () => {
      const status = codegen.getStatus();
      expect(status.initialized).toBe(false);
    });
  });

  describe('getStatus', () => {
    it('should return correct initial status', () => {
      const status = codegen.getStatus();
      expect(status.initialized).toBe(false);
      expect(status.plugins.discovered).toBe(0);
      expect(status.plugins.loaded).toBe(0);
      expect(status.registries.plugins).toBe(0);
      expect(status.registries.aggregates).toBe(0);
      expect(status.registries.specs).toBe(0);
      expect(status.options.outputDir).toBe('./test-output');
      expect(status.options.strictMode).toBe(false);
      expect(status.options.verbose).toBe(false);
      expect(status.options.enableCache).toBe(false);
    });
  });

  describe('register', () => {
    it('should register plugin components', () => {
      const mockComponent = { id: 'test-plugin' };
      codegen.register('plugin', 'test-plugin', mockComponent);

      expect(codegen.get('plugin', 'test-plugin')).toBe(mockComponent);
    });

    it('should register aggregate components', () => {
      const mockComponent = { id: 'test-aggregate' };
      codegen.register('TestAggregate', 'test-component', mockComponent);

      expect(codegen.get('TestAggregate', 'test-component')).toBe(mockComponent);
    });
  });

  describe('get', () => {
    it('should return registered component', () => {
      const mockComponent = { id: 'test-component' };
      codegen.register('plugin', 'test-component', mockComponent);

      expect(codegen.get('plugin', 'test-component')).toBe(mockComponent);
    });

    it('should return null for non-existent component', () => {
      expect(codegen.get('plugin', 'nonexistent')).toBeNull();
    });
  });

  describe('list', () => {
    it('should list registered components', () => {
      const component1 = { id: 'component1' };
      const component2 = { id: 'component2' };
      codegen.register('plugin', 'component1', component1);
      codegen.register('plugin', 'component2', component2);

      const list = codegen.list('plugin');
      expect(list).toContain('component1');
      expect(list).toContain('component2');
    });

    it('should return empty array for empty registry', () => {
      expect(codegen.list('plugin')).toEqual([]);
    });
  });

  describe('drillDown', () => {
    it('should return null when no root aggregate exists', () => {
      expect(codegen.drillDown(['test'])).toBeNull();
    });

    it('should return null for empty path', () => {
      expect(codegen.drillDown([])).toBeNull();
    });
  });

  describe('getRootAggregate', () => {
    it('should return undefined when no root aggregate is set', () => {
      expect(codegen.getRootAggregate()).toBeUndefined();
    });
  });

  describe('execute', () => {
    it('should throw error when not initialized', async () => {
      const context = { operation: 'test' };
      await expect(codegen.execute(context)).rejects.toThrow(
        'Codegen system not initialized. Call initialize() first.'
      );
    });
  });

  describe('initialize', () => {
    it('should initialize the system successfully', async () => {
      // Test that initialize can be called without throwing
      await expect(codegen.initialize()).resolves.toBeDefined();
    });
  });
});

#!/usr/bin/env node

/**
 * BaseCodegen - Core foundation for the codegen platform
 * Implements plugin discovery, registries, aggregates, and spec-driven generation
 * Follows AGENTS.md architectural patterns
 * TypeScript strict typing with no 'any' types
 */

import * as fs from 'fs';
import * as path from 'path';
import type {
  IAggregate,
  IRegistry,
  IBaseCodegenOptions,
  IPlugin,
  ISpec,
  IComponent,
} from './interfaces/index';

/**
 *
 */
interface PluginInfo {
  id: string;
  name: string | undefined;
  version: string | undefined;
  entry_point: string;
  path: string;
  category: string;
  [key: string]: unknown;
}

/**
 *
 */
interface ExecutionResults {
  success: boolean;
  generated: string[];
  errors: string[];
  warnings: string[];
  timestamp: string;
  stats: {
    pluginsExecuted: number;
    specsProcessed: number;
    filesGenerated: number;
    [key: string]: number;
  };
}

/**
 *
 */
interface AggregateResults {
  generated: string[];
  errors: string[];
  warnings: string[];
  stats: {
    pluginsExecuted: number;
    specsProcessed: number;
    filesGenerated: number;
    [key: string]: number;
  };
}

/**
 *
 */
interface SystemStatus {
  initialized: boolean;
  plugins: {
    discovered: number;
    loaded: number;
  };
  registries: {
    plugins: number;
    aggregates: number;
    specs: number;
  };
  options: IBaseCodegenOptions;
}

/**
 *
 */
export abstract class BaseCodegen {
  protected options: IBaseCodegenOptions;
  protected pluginRegistry: Map<string, IPlugin>;
  protected aggregateRegistry: Map<string, IAggregate | IRegistry | Map<string, IComponent>>;
  protected specRegistry: Map<string, ISpec>;
  protected discoveredPlugins: Map<string, PluginInfo>;
  protected loadedPlugins: Map<string, IPlugin>;
  protected initialized: boolean;
  protected specs: Map<string, ISpec>;
  protected templates: Map<string, unknown>;

  constructor(options: IBaseCodegenOptions = {}) {
    this.options = {
      outputDir: options.outputDir ?? './generated',
      strictMode: options.strictMode ?? true,
      verbose: options.verbose ?? false,
      enableCache: options.enableCache ?? true,
      ...options,
    };

    // Core registries (immutable after initialization)
    this.pluginRegistry = new Map();
    this.aggregateRegistry = new Map();
    this.specRegistry = new Map();

    // Plugin discovery state
    this.discoveredPlugins = new Map();
    this.loadedPlugins = new Map();

    // Runtime state
    this.initialized = false;
    this.specs = new Map();
    this.templates = new Map();
  }

  /**
   * Initialize the codegen system
   * @returns Promise<BaseCodegen> The initialized system
   */
  public async initialize(): Promise<BaseCodegen> {
    if (this.initialized) {
      return this;
    }

    this._ensureNotInitialized();

    try {
      this.log('Initializing Codegen Core...', 'info');

      // Discover and load plugins
      this._discoverPlugins();
      await this._loadPlugins();

      // Initialize registries
      this._initializeRegistries();

      // Load specifications
      await this._loadSpecs();

      this._markInitialized();
      this.log('Codegen Core initialized successfully', 'success');

      return this;
    } catch (error) {
      this.log(`Codegen initialization failed: ${(error as Error).message}`, 'error');
      throw error;
    }
  }

  /**
   * Execute codegen operation
   * @param context - Execution context
   * @returns Promise<ExecutionResults> Generation results
   */
  public async execute(context: Record<string, unknown>): Promise<ExecutionResults> {
    this._ensureInitialized();

    const results: ExecutionResults = {
      success: false,
      generated: [],
      errors: [],
      warnings: [],
      timestamp: new Date().toISOString(),
      stats: {
        pluginsExecuted: 0,
        specsProcessed: 0,
        filesGenerated: 0,
      },
    };

    try {
      this.log('Executing codegen operation...', 'info');

      // Process through aggregates
      const aggregateResults = await this._executeAggregates(context);

      // Merge results
      results.generated = aggregateResults.generated;
      results.errors = aggregateResults.errors;
      results.warnings = aggregateResults.warnings;
      results.stats = { ...results.stats, ...aggregateResults.stats };
      results.success = results.errors.length === 0;

      this.log(
        `Codegen execution ${results.success ? 'successful' : 'completed with errors'}`,
        results.success ? 'success' : 'warning'
      );

      return results;
    } catch (error) {
      results.errors.push((error as Error).message);
      this.log(`Codegen execution failed: ${(error as Error).message}`, 'error');
      return results;
    }
  }

  /**
   * Discover plugins from filesystem
   * @returns Promise<void>
   */
  protected _discoverPlugins(): void {
    this.log('Discovering plugins...', 'info');

    const pluginsDir = path.join(__dirname, '../plugins');

    // Discover from each plugin category
    const categories = ['tools', 'languages', 'templates', 'profiles'];

    for (const category of categories) {
      const categoryDir = path.join(pluginsDir, category);

      if (!fs.existsSync(categoryDir)) {
        continue;
      }

      const items = fs.readdirSync(categoryDir);

      for (const item of items) {
        const pluginDir = path.join(categoryDir, item);
        const manifestPath = path.join(pluginDir, 'plugin.json');

        if (fs.existsSync(manifestPath)) {
          try {
            const manifest = JSON.parse(fs.readFileSync(manifestPath, 'utf8')) as Record<
              string,
              unknown
            >;
            const pluginId = manifest.id as string;
            const entryPoint = manifest.entry_point as string;
            this.discoveredPlugins.set(pluginId, {
              id: pluginId,
              name: manifest.name as string | undefined,
              version: manifest.version as string | undefined,
              entry_point: entryPoint,
              path: pluginDir,
              category,
            });
          } catch (error) {
            this.log(
              `Failed to load plugin manifest ${manifestPath}: ${(error as Error).message}`,
              'warning'
            );
          }
        }
      }
    }

    this.log(`Discovered ${this.discoveredPlugins.size} plugins`, 'info');
  }

  /**
   * Load discovered plugins
   * @returns Promise<void>
   */
  protected async _loadPlugins(): Promise<void> {
    this.log('Loading plugins...', 'info');

    // Load plugins in dependency order (simplified - no topological sort yet)
    for (const [pluginId, pluginInfo] of this.discoveredPlugins) {
      try {
        const entryPoint = path.join(pluginInfo.path, pluginInfo.entry_point);
        // Note: require() is used here as plugins may be CommonJS or ESM
        // eslint-disable-next-line @typescript-eslint/no-unsafe-assignment, @typescript-eslint/no-unsafe-call
        const PluginClass = require(entryPoint);

        // Create plugin instance
        // eslint-disable-next-line @typescript-eslint/no-unsafe-assignment, @typescript-eslint/no-unsafe-call
        const plugin = new PluginClass();

        // Initialize plugin
        if (typeof plugin.initialize === 'function') {
          // eslint-disable-next-line @typescript-eslint/no-unsafe-member-access, @typescript-eslint/no-unsafe-call
          await plugin.initialize();
        }

        // Register plugin
        if (typeof plugin.register === 'function') {
          // eslint-disable-next-line @typescript-eslint/no-unsafe-member-access, @typescript-eslint/no-unsafe-call
          await plugin.register(this);
        }

        this.loadedPlugins.set(pluginId, plugin);
        this.log(`Loaded plugin: ${pluginId}`, 'success');
      } catch (error) {
        this.log(`Failed to load plugin ${pluginId}: ${(error as Error).message}`, 'error');
        if (this.options.strictMode) {
          throw error;
        }
      }
    }

    this.log(`Successfully loaded ${this.loadedPlugins.size} plugins`, 'info');
  }

  /**
   * Initialize registries and aggregates
   * @returns Promise<void>
   */
  protected _initializeRegistries(): void {
    this.log('Initializing registries and aggregates...', 'info');

    // Create core aggregates as defined in AGENTS.md
    const aggregates: Record<string, { children: string[] }> = {
      AppAggregate: {
        children: [
          'DomainAggregate',
          'AdaptersAggregate',
          'CodegenAggregate',
          'I18nAggregate',
          'ToolingAggregate',
        ],
      },
      ToolingAggregate: {
        children: [
          'PackageManagersRegistry',
          'BuildSystemsRegistry',
          'DevWorkflowRegistry',
          'QARegistry',
          'SDKRegistry',
          'AppsRegistry',
          'ProfilesRegistry',
        ],
      },
      CodegenAggregate: {
        children: ['LanguagesRegistry', 'SnippetsRegistry', 'TemplatesRegistry'],
      },
      // Add other aggregates as needed
    };

    // Initialize aggregates
    for (const [aggregateId, config] of Object.entries(aggregates)) {
      this.aggregateRegistry.set(aggregateId, {
        listChildren: () => config.children,
        getChild: (id: string) => {
          const aggregate = this.aggregateRegistry.get(id);
          if (aggregate && !(aggregate instanceof Map)) {
            return aggregate;
          }
          return null;
        },
        describe: () => null,
      });
    }

    this.log('Registries and aggregates initialized', 'success');
  }

  /**
   * Load specifications from plugins
   * @returns Promise<void>
   */
  protected async _loadSpecs(): Promise<void> {
    this.log('Loading specifications...', 'info');

    for (const [pluginId, plugin] of this.loadedPlugins) {
      if (typeof (plugin as any).getSpec === 'function') {
        try {
          const spec = await (plugin as any).getSpec();
          if (spec) {
            this.specRegistry.set((spec as any).id || pluginId, spec);
            this.log(`Loaded spec for plugin: ${pluginId}`, 'info');
          }
        } catch (error) {
          this.log(
            `Failed to load spec for plugin ${pluginId}: ${(error as Error).message}`,
            'warning'
          );
        }
      }
    }

    this.log(`Loaded ${this.specRegistry.size} specifications`, 'info');
  }

  /**
   * Execute aggregates for code generation
   * @param context - Execution context
   * @returns Promise<AggregateResults> Aggregate execution results
   */
  protected async _executeAggregates(context: Record<string, unknown>): Promise<AggregateResults> {
    const results: AggregateResults = {
      generated: [],
      errors: [],
      warnings: [],
      stats: {
        pluginsExecuted: 0,
        specsProcessed: 0,
        filesGenerated: 0,
      },
    };

    // Execute plugins based on context
    for (const [pluginId, plugin] of this.loadedPlugins) {
      try {
        if (typeof (plugin as any).execute === 'function') {
          const pluginResults = await (plugin as any).execute(context);
          results.generated.push(...((pluginResults as any).generated || []));
          results.errors.push(...((pluginResults as any).errors || []));
          results.warnings.push(...((pluginResults as any).warnings || []));
          results.stats.pluginsExecuted++;
        }
      } catch (error) {
        results.errors.push(`Plugin ${pluginId} execution failed: ${(error as Error).message}`);
        if (this.options.strictMode) {
          throw error;
        }
      }
    }

    return results;
  }

  /**
   * Register a component in the appropriate registry
   * @param registryId - Registry identifier
   * @param componentId - Component identifier
   * @param component - Component to register
   */
  public register(registryId: string, componentId: string, component: unknown): void {
    if (registryId === 'plugin') {
      this.pluginRegistry.set(componentId, component as IPlugin);
    } else {
      // Handle other registry types
      let registry = this.aggregateRegistry.get(registryId);
      if (!registry) {
        registry = new Map();
        this.aggregateRegistry.set(registryId, registry);
      }
      if (registry instanceof Map) {
        registry.set(componentId, component as IComponent);
      }
      // Note: IAggregate/IRegistry interfaces don't have set methods
      // This would need proper implementation of registry classes
    }
  }

  /**
   * Get a component from registries
   * @param registryId - Registry identifier
   * @param componentId - Component identifier
   * @returns The component or null if not found
   */
  public get(registryId: string, componentId: string): unknown {
    if (registryId === 'plugin') {
      return this.pluginRegistry.get(componentId) || null;
    }

    const registry = this.aggregateRegistry.get(registryId);
    if (registry instanceof Map) {
      return registry.get(componentId) || null;
    }
    if (registry && typeof (registry as IRegistry).get === 'function') {
      return (registry as IRegistry).get(componentId);
    }
    return null;
  }

  /**
   * List components in a registry
   * @param registryId - Registry identifier
   * @returns Array of component IDs
   */
  public list(registryId: string): string[] {
    if (registryId === 'plugin') {
      return Array.from(this.pluginRegistry.keys());
    }

    const registry = this.aggregateRegistry.get(registryId);
    if (registry instanceof Map) {
      return Array.from(registry.keys());
    }
    if (registry && typeof (registry as IRegistry).listIds === 'function') {
      return Array.from((registry as IRegistry).listIds());
    }
    return [];
  }

  /**
   * Get root aggregate (AppAggregate)
   * @returns Root aggregate
   */
  public getRootAggregate(): IAggregate | IRegistry | undefined {
    const aggregate = this.aggregateRegistry.get('AppAggregate');
    return aggregate instanceof Map ? undefined : aggregate;
  }

  /**
   * Drill down through aggregates
   * @param path - Path through aggregates
   * @returns Target component or null
   */
  public drillDown(path: string[]): IAggregate | IRegistry | null {
    let current: IAggregate | IRegistry | undefined = this.getRootAggregate();

    for (const segment of path) {
      if (current && typeof (current as any).getChild === 'function') {
        current = (current as any).getChild(segment);
      } else {
        return null;
      }
    }

    return current || null;
  }

  /**
   * Log a message with appropriate formatting
   * @param message - Message to log
   * @param level - Log level
   */
  public log(message: string, level: string = 'info'): void {
    if (!this.options.verbose && level === 'info') {
      return;
    }

    const timestamp = new Date().toISOString().split('T')[1].split('.')[0];
    const prefix = `[${timestamp}] [Codegen]`;

    switch (level) {
      case 'success':
        console.log(`\x1b[32m${prefix} ‚úì ${message}\x1b[0m`);
        break;
      case 'warning':
        console.log(`\x1b[33m${prefix} ‚ö† ${message}\x1b[0m`);
        break;
      case 'error':
        console.log(`\x1b[31m${prefix} ‚úó ${message}\x1b[0m`);
        break;
      default:
        console.log(`${prefix} ${message}`);
    }
  }

  /**
   * Ensure system is not already initialized
   * @throws Error If already initialized
   */
  protected _ensureNotInitialized(): void {
    if (this.initialized) {
      throw new Error('Codegen system already initialized');
    }
  }

  /**
   * Ensure system is initialized
   * @throws Error If not initialized
   */
  protected _ensureInitialized(): void {
    if (!this.initialized) {
      throw new Error('Codegen system not initialized. Call initialize() first.');
    }
  }

  /**
   * Mark system as initialized
   */
  protected _markInitialized(): void {
    this.initialized = true;
  }

  /**
   * Get system status
   * @returns System status
   */
  public getStatus(): SystemStatus {
    return {
      initialized: this.initialized,
      plugins: {
        discovered: this.discoveredPlugins.size,
        loaded: this.loadedPlugins.size,
      },
      registries: {
        plugins: this.pluginRegistry.size,
        aggregates: this.aggregateRegistry.size,
        specs: this.specRegistry.size,
      },
      options: this.options,
    };
  }
}

