      [
        'missing id',
        { uuid: '12345678-1234-4123-8123-123456789012', id: '', search: { keywords: ['test'] } },
        'Missing required fields: id, search',
      ],
      [
        'missing search',
        { uuid: '12345678-1234-4123-8123-123456789012', id: 'test-id', search: undefined },
        'Missing required fields: id, search',
      ],
    ])(
      'should throw error for %s',
