      expect(result).toEqual({
        success: true,
        component: 'test-component',
        timestamp: expect.any(String),
      });
    });
  });

  describe('validate', () => {
    it.each([
      ['empty object', {}, true],
      ['object with properties', { key: 'value' }, true],
      ['array', [], true],
    ])('should return %s for %s', (expected: string, input: any, result: boolean) => {
      expect(component.validate(input)).toBe(result);
    });

    it.each([
      ['null', null, false],
      ['undefined', undefined, false],
    ])('should return false for %s', (description: string, input: any, expected: boolean) => {
      expect(component.validate(input)).toBe(expected);
    });

    it.each([
      ['string', 'string', false],
      ['number', 42, false],
      ['boolean', true, false],
    ])('should return false for %s input', (description: string, input: any, expected: boolean) => {
      expect(component.validate(input)).toBe(expected);
    });
  });
});

/**
 * BaseComponent - Foundation for all AGENTS.md compliant components
 * Strict OO constraints: 1 constructor param, ‚â§3 public methods, ‚â§10 lines per function
 * TypeScript strict typing with no 'any' types
 */

import type { IComponent, ISearchMetadata, ISpec } from './interfaces/index';

/**
 *
 */
export abstract class BaseComponent implements IComponent {
  public readonly uuid: string;
  public readonly id: string;
  public readonly search: ISearchMetadata;
  public readonly spec: ISpec;

  /**
   * Constructor with single spec parameter (AGENTS.md requirement)
   * @param spec - Component specification with uuid, id, search metadata
   */
  constructor(spec: ISpec) {
    this.uuid = spec.uuid;
    this.id = spec.id;
    this.search = spec.search;
    this.spec = spec;
  }

  /**
   * Initialise component (lifecycle method, ‚â§10 lines)
   * @returns Promise<BaseComponent> Initialised component
   */
  public async initialise(): Promise<IComponent> {
    // Validate UUID format
    if (!this.isValidUUID(this.uuid)) {
      throw new Error(`Invalid UUID: ${this.uuid}`);
    }
    // Validate required fields
    if (!this.id) {
      throw new Error('Missing required field: id');
    }
    if (!this.search.title) {
      throw new Error('Missing required field: search.title');
    }
    return await Promise.resolve(this);
  }

  /**
   * Execute component logic (core method, ‚â§10 lines)
   * @param _context - Execution context
   * @returns Execution result
   */
  public async execute(_context: Record<string, unknown>): Promise<unknown> {
    return await Promise.resolve({
      success: true,
      component: this.id,
      timestamp: new Date().toISOString(),
    });
  }

  /**
   * Validate input (optional method, keeps ‚â§3 total public methods)
   * @param input - Input to validate
   * @returns Validation result
   */
  public validate(input: unknown): boolean {
    return input !== null && input !== undefined && typeof input === 'object';
  }

  /**
   * Validate UUID format (private helper, ‚â§10 lines)
   * @param uuid - UUID to validate
   * @returns True if valid UUID
   */
  private isValidUUID(uuid: string): boolean {
    const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-4[0-9a-f]{3}-[89ab][0-9a-f]{3}-[0-9a-f]{12}$/i;
    return typeof uuid === 'string' && uuidRegex.test(uuid);
  }
}

