  describe('shutdown', () => {
    it('should reset initialized state', async () => {
      // First initialize
      const mockSpec: ISpec = {
        uuid: 'test-uuid-123',
        id: 'test-plugin',
        type: 'plugin',
        search: {
          title: 'Test Plugin',
