      // Simulate adding new component
      const newComponent = new MockComponent('new-uuid', 'new-comp', { title: 'New Component' });
      (registry as any).components.set('new-comp', newComponent);

      expect(registry.listIds()).toContain('new-comp');
      expect(registry.get('new-comp')).toBe(newComponent);
      expect(registry.describe('new-uuid')).toBe(newComponent.search);

      // Simulate removing component
      (registry as any).components.delete('comp2');
      expect(registry.listIds()).not.toContain('comp2');
      expect(registry.get('comp2')).toBeNull();
      expect(registry.describe('22222222-2222-2222-2222-222222222222')).toBeNull();
    });

    it.each([
      ['ID lookup', 'comp1', 'ID'],
      ['UUID lookup', '11111111-1111-1111-1111-111111111111', 'UUID'],
    ])('should support both ID and UUID lookup for %s', (description, identifier, lookupType) => {
      const component = registry.get(identifier);
      expect(component).toBe(mockComponent1);
      expect(component?.id).toBe('comp1');
      expect(component?.uuid).toBe('11111111-1111-1111-1111-111111111111');
    });

    it('should maintain component immutability', () => {
      const component = registry.get('comp1');
      expect(component).toBe(mockComponent1);

      // Verify components are not modifiable through registry
      const list1 = registry.listIds();
      const list2 = registry.listIds();
      expect(list1).not.toBe(list2); // Different array instances
      expect(list1).toEqual(list2); // Same content
    });

    it('should handle edge cases gracefully', () => {
      // Test with various invalid inputs
      expect(registry.get('')).toBeNull();
      expect(registry.get(null as any)).toBeNull();
      expect(registry.get(undefined as any)).toBeNull();
      expect(registry.describe('')).toBeNull();
      expect(registry.describe(null as any)).toBeNull();
      expect(registry.describe(undefined as any)).toBeNull();

      // Registry should remain functional
      expect(registry.listIds()).toEqual(['comp1', 'comp2', 'comp3']);
      expect(registry.get('comp1')).toBe(mockComponent1);
    });
  });
});

/**
 * Registry - AGENTS.md compliant component registry
 * Strict OO constraints: ‚â§3 public methods, immutable after construction
 * TypeScript strict typing with no 'any' types
 */

import { BaseComponent } from './base-component';
import type { IRegistry, IComponent } from './interfaces/index';
import type { ISearchMetadata } from './interfaces/isearch-metadata';
import type { ISpec } from './interfaces/ispec';

/**
 *
 */
export abstract class Registry extends BaseComponent implements IRegistry {
  protected components: Map<string, IComponent>;
  public readonly componentType: string;

  /**
   * Constructor with single spec parameter (AGENTS.md requirement)
   * @param spec - Registry specification
   */
  constructor(spec: ISpec) {
    super(spec);
    this.components = new Map();
    this.componentType = spec.componentType || 'component';
  }

  /**
   * List all component IDs (‚â§3 public methods, ‚â§10 lines)
   * @returns Array of component IDs
   */
  public listIds(): readonly string[] {
    return Array.from(this.components.keys());
  }

  /**
   * Get component by ID or UUID (‚â§3 public methods, ‚â§10 lines)
   * @param idOrUuid - Component ID or UUID
   * @returns Component or null if not found
   */
  public get(idOrUuid: string): IComponent | null {
    // Try direct ID lookup first
    if (this.components.has(idOrUuid)) {
      return this.components.get(idOrUuid) || null;
    }
    // Fallback to UUID lookup
    for (const component of this.components.values()) {
      if (component.uuid === idOrUuid) {
        return component;
      }
    }
    return null;
  }

  /**
   * Register component (protected method for subclasses)
   * @param id - Component ID
   * @param component - Component to register
   */
  protected _register(id: string, component: IComponent): void {
    this.components.set(id, component);
  }

  /**
   * Validate component before registration (‚â§10 lines)
   * @param component - Component to validate
   * @returns Is valid
   */
  protected _validateComponent(component: unknown): boolean {
    return (
      component !== null &&
      component !== undefined &&
      typeof component === 'object' &&
      'uuid' in component &&
      'id' in component &&
      'search' in component &&
      this.isValidUUID((component as any).uuid)
    );
  }

  /**
   * Describe component for search/discovery
   * @param idOrUuid - Component ID or UUID
   * @returns Search metadata or null
   */
  public describe(idOrUuid: string): ISearchMetadata | null {
    const component = this.get(idOrUuid);
    return component ? component.search : null;
  }

  /**
   * Validate UUID format (private helper)
   * @param uuid - UUID to validate
   * @returns True if valid UUID
   */
  private isValidUUID(uuid: string): boolean {
    const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-4[0-9a-f]{3}-[89ab][0-9a-f]{3}-[0-9a-f]{12}$/i;
    return typeof uuid === 'string' && uuidRegex.test(uuid);
  }
}

import { describe, it, expect, beforeEach, vi } from 'vitest';
import { SpecValidator } from './spec-validator';
import * as fs from 'fs';
import * as path from 'path';

// Mock filesystem operations
vi.mock('fs');
vi.mock('path');

const mockedFs = vi.mocked(fs);
const mockedPath = vi.mocked(path);

describe('SpecValidator', () => {
  let validator: SpecValidator;
  const mockSchemaPath = '/mock/schema.json';
  const mockSchema = {
    type: 'object',
    properties: {
      uuid: { type: 'string' },
      id: { type: 'string' },
      search: { type: 'object' },
    },
    required: ['uuid', 'id', 'search'],
  };

  beforeEach(() => {
    vi.clearAllMocks();
    mockedPath.join.mockReturnValue(mockSchemaPath);
    mockedFs.existsSync.mockReturnValue(true);
    mockedFs.readFileSync.mockReturnValue(JSON.stringify(mockSchema));

    validator = new SpecValidator();
  });

  describe('constructor', () => {
    it.each([
      ['default options', undefined, '/mock/schema.json', true],
      ['custom schema path', { schemaPath: '/custom/path.json' }, '/custom/path.json', true],
      ['strict mode disabled', { strictMode: false }, '/mock/schema.json', false],
      ['strict mode enabled', { strictMode: true }, '/mock/schema.json', true],
    ])('should initialize with %s', (description, options, expectedPath, expectedStrict) => {
      const testValidator = new SpecValidator(options);
      expect(mockedPath.join).toHaveBeenCalled();

      // Test that validator can be initialized (schema loading)
      expect(() => testValidator).not.toThrow();
    });
  });

  describe('initialise', () => {
    it.each([
      ['successful schema load', true, JSON.stringify(mockSchema), undefined],
      ['schema file not found', false, null, 'ENOENT'],
      ['invalid JSON schema', true, '{invalid json}', 'SyntaxError'],
    ])('should handle %s', async (description, fileExists, fileContent, expectedError) => {
      mockedFs.existsSync.mockReturnValue(fileExists);

      if (fileContent !== null) {
        mockedFs.readFileSync.mockReturnValue(fileContent);
      } else {
        mockedFs.readFileSync.mockImplementation(() => {
          throw new Error(expectedError);
        });
      }

      if (expectedError) {
        await expect(validator.initialise()).rejects.toThrow();
      } else {
        await expect(validator.initialise()).resolves.not.toThrow();
        expect(mockedFs.readFileSync).toHaveBeenCalledWith(mockSchemaPath, 'utf8');
