  IValidationResult,
  ISpecForValidation,
  ISearchMetadataForValidation,
} from './interfaces/index';

/**
 *
 */
export class SpecValidator {
  private readonly schemaPath: string;
  private readonly strictMode: boolean;
  private schema: unknown | null;

  /**
   * Constructor with single options parameter
   * @param options - Validator options
   */
  constructor(options: ISpecValidatorOptions = {}) {
    this.schemaPath = options.schemaPath || path.join(__dirname, '../schemas/spec-schema.json');
    this.strictMode = options.strictMode !== false;
    this.schema = null;
  }

  /**
   * Initialise validator (loads schema)
   * @returns Promise<SpecValidator> Initialised validator
   */
  public async initialise(): Promise<SpecValidator> {
    this.schema = JSON.parse(fs.readFileSync(this.schemaPath, 'utf8'));
    return this;
  }

  /**
   * Validate specification
   * @param spec - Specification to validate
   * @returns Validation result
   */
  public validate(spec: unknown): IValidationResult {
    const errors: string[] = [];

    // Basic structure validation
    if (!this._validateBasicStructure(spec, errors)) {
      return { valid: false, errors };
    }

    // AGENTS.md specific validations
    if (!this._validateAgentsMdCompliance(spec as ISpecForValidation, errors)) {
      return { valid: false, errors };
    }

    return { valid: true, errors: [] };
  }

  /**
   * Validate basic structure
   * @param spec - Spec to validate
   * @param errors - Error accumulator
   * @returns Is valid
   */
  private _validateBasicStructure(spec: unknown, errors: string[]): boolean {
    if (!spec || typeof spec !== 'object') {
      errors.push('Spec must be an object');
      return false;
    }

    const specObj = spec as Record<string, unknown>;
    const required = ['uuid', 'id', 'search'];
    for (const field of required) {
      if (!(field in specObj)) {
        errors.push(`Missing required field: ${field}`);
      }
    }

    return errors.length === 0;
  }

  /**
   * Validate AGENTS.md compliance
   * @param spec - Spec to validate
   * @param errors - Error accumulator
   * @returns Is valid
   */
  private _validateAgentsMdCompliance(spec: ISpecForValidation, errors: string[]): boolean {
    // UUID format validation
    if (!this._isValidUUID(spec.uuid)) {
      errors.push('Invalid UUID format (must be RFC 4122)');
    }

    // Search metadata validation
    if (!this._validateSearchMetadata(spec.search, errors)) {
      return false;
    }

    // ID format validation
    if (!this._isValidId(spec.id)) {
      errors.push('Invalid ID format (must match pattern)');
    }

    return errors.length === 0;
  }

  /**
   * Validate search metadata
   * @param search - Search metadata
   * @param errors - Error accumulator
   * @returns Is valid
   */
  private _validateSearchMetadata(search: ISearchMetadataForValidation, errors: string[]): boolean {
    if (!search || typeof search !== 'object') {
      errors.push('Search metadata must be an object');
      return false;
    }

    const required = ['title', 'summary', 'keywords'];
    for (const field of required) {
      if (!(field in search)) {
        errors.push(`Missing required search field: ${field}`);
      }
    }

    if (search.keywords && !Array.isArray(search.keywords)) {
      errors.push('Search keywords must be an array');
    }

    return errors.length === 0;
  }

  /**
   * Validate UUID format
   * @param uuid - UUID to validate
   * @returns Is valid
   */
  private _isValidUUID(uuid: string): boolean {
    const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-4[0-9a-f]{3}-[89ab][0-9a-f]{3}-[0-9a-f]{12}$/i;
    return typeof uuid === 'string' && uuidRegex.test(uuid);
  }

  /**
   * Validate ID format
   * @param id - ID to validate
   * @returns Is valid
   */
  private _isValidId(id: string): boolean {
    const idRegex = /^[a-z][a-z0-9.-]*$/;
    return typeof id === 'string' && idRegex.test(id);
  }
}

#!/usr/bin/env node

/**
 * CodegenEntrypoint - Main CLI/web entry point
 * Revolutionary Codegen system entry point with lifecycle management
 * TypeScript strict typing with no 'any' types
 */

import { BaseComponent } from '../core/base-component';
import type { IAggregator } from '../core/interfaces/index';

/**
 *
 */
interface CodegenOptions {
  outputDir?: string;
  verbose?: boolean;
  strictMode?: boolean;
  [key: string]: unknown;
}

/**
 *
 */
interface CLIArgs {
  spec?: string;
  output?: string;
  language?: string;
  profile?: string;
  template?: string;
  category?: string;
  component?: string;
  query?: string;
  tool?: string;
  platform?: string;
  packageManager?: string;
  [key: string]: unknown;
}

/**
 *
 */
export class CodegenEntrypoint extends BaseComponent {
  private readonly aggregator: IAggregator;

  constructor(aggregator: IAggregator, options: CodegenOptions = {}) {
    super({
      uuid: '12345678-1234-4123-8123-123456789012',
      id: 'codegen-entrypoint',
      type: 'entrypoint',
      search: {
        title: 'CodegenEntrypoint',
        summary: 'Main entry point for Revolutionary Codegen',
        keywords: ['codegen', 'entrypoint', 'cli'],
        domain: 'core',
        capabilities: ['cli', 'navigation'],
      },
    });

    this.aggregator = aggregator;
  }

  /**
   *
   */
  public override async initialise(): Promise<CodegenEntrypoint> {
    await super.initialise();
    return this;
  }

  /**
   *
   * @param args
   */
  public async runCLI(args: string[]): Promise<void> {
    const command = args[0] || 'help';
    const options = this._parseCLIArgs(args.slice(1));

    try {
      await this.initialise();

      switch (command) {
        case 'generate':
          await this._runGenerate(options);
          break;
        case 'list':
          await this._runList(options);
          break;
        case 'describe':
          await this._runDescribe(options);
          break;
        case 'search':
          await this._runSearch(options);
          break;
        default:
          this._displayHelp();
      }
    } catch (error) {
      throw new Error(`CLI execution failed: ${(error as Error).message}`);
    }
  }

  /**
   *
   */
  public getAggregator(): IAggregator {
    return this.aggregator;
  }

  /**
   *
   * @param path
   */
  public drillDown(path: string[]): IAggregator | null {
    return this.aggregator.drillDown(path) as IAggregator;
  }

  /**
   *
   * @param options
   */
  private async _runGenerate(options: CLIArgs): Promise<void> {
    const context = {
      operation: 'generate',
      specPath: options.spec,
      outputDir: options.output,
      language: options.language,
      profile: options.profile,
      template: options.template,
    };

    const target = this.drillDown(['ExecutionAggregator']);
    if (target) {
      await target.execute(context);
    }
  }

  /**
   *
   * @param options
   */
  private async _runList(options: CLIArgs): Promise<void> {
    const category = options.category || 'all';

    if (category === 'plugins' || category === 'all') {
      const plugins = this.drillDown(['PluginAggregator']);
      if (plugins) {
        plugins.listChildren();
      }
    }

    if (category === 'tools' || category === 'all') {
      const tools = this.drillDown(['ToolingAggregate', 'DevWorkflowRegistry']);
      if (tools) {
        tools.listChildren();
      }
    }
  }

  /**
   *
   * @param options
   */
  private async _runDescribe(options: CLIArgs): Promise<void> {
    const componentId = options.component;
    if (!componentId) {
      return;
    }

    const aggregators = ['PluginAggregator', 'ToolingAggregate'];
    for (const agg of aggregators) {
      const component = this.drillDown([agg, componentId]);
      if (component) {
        return;
      }
    }
  }

  /**
   *
   * @param options
   */
  private async _runSearch(options: CLIArgs): Promise<void> {
    const query = options.query;
    if (!query) {
      return;
    }
  }

  /**
   *
   * @param args
   */
  private _parseCLIArgs(args: string[]): CLIArgs {
    const options: CLIArgs = {};
    for (let i = 0; i < args.length; i++) {
      const arg = args[i];
      if (arg.startsWith('--')) {
        const key = arg.slice(2);
        options[key] = args[i + 1] && !args[i + 1].startsWith('--') ? args[++i] : true;
      } else if (!options.component) {
        options.component = arg;
      }
    }
    return options;
  }

  /**
   *
   */
  private _displayHelp(): void {
    // Help display functionality
  }
}

/**
 * Clean Tool Plugin - AGENTS.md compliant TypeScript implementation
 * Single responsibility: Cleanup of generated code and dependencies
 */

import type { IPluginConfig, IRegistryManager } from '../../../../core/interfaces/index';
import { BasePlugin } from '../../../../core/base-plugin';
import { rmSync, existsSync, statSync, readdirSync } from 'fs';
import { join, resolve } from 'path';

/**
 *
 */
export class CleanPlugin extends BasePlugin {
  private verified = false;

  /**
   * Constructor with single config parameter
   * @param config - Plugin configuration
   */
  constructor(config: IPluginConfig) {
    super(config);
    // Override the base path to point to plugin root directory
    (this as any).specLoader =
      new (require('../../../../core/plugin-spec-loader').PluginSpecLoader)(join(__dirname, '..'));
    (this as any).messageLoader =
      new (require('../../../../core/plugin-message-loader').PluginMessageLoader)(
        join(__dirname, '..')
      );
  }

  /**
   * Register Clean tool with registries
   * @param registryManager - Registry manager instance
   */
  public override async register(registryManager: IRegistryManager): Promise<void> {
    // Initialize first to load the spec with proper UUID
    await this.initializePlugin();
    registryManager.register('DevWorkflowRegistry', 'tool.dev.clean', this);
  }

  /**
   * Execute cleanup operations (single business method)
   * @param context - Execution context
   * @returns Cleanup results
   */
  public override async execute(context: Record<string, unknown>): Promise<unknown> {
    const operation = (context.operation as string) || 'execute';

    switch (operation) {
      case 'execute':
        return this.executeCleanup(context);
      case 'verify':
        return this.verifyClean(context);
      case 'help':
        return this.getCleanHelp(context);
      default:
        throw new Error(`Unknown Clean operation: ${operation}`);
    }
  }

  /**
   * Execute cleanup operations
   * @param context - Cleanup context
   * @returns Cleanup results
   */
  private executeCleanup(context: Record<string, unknown>): unknown {
    const {
      targets = 'node_modules,dist,build,out,.cache,.next,coverage',
      dryRun = false,
      force = false,
    } = context;

    const targetPatterns = (targets as string).split(',').map((t) => t.trim());
    const results: Array<{ target: string; removed: boolean; error?: string }> = [];

    for (const pattern of targetPatterns) {
      try {
        const removed = this.cleanTarget(pattern, !!dryRun, !!force);
        results.push({ target: pattern, removed });
      } catch (error) {
        results.push({
          target: pattern,
          removed: false,
          error: (error as Error).message,
        });
      }
    }

    return {
      operation: 'cleanup',
      targets: targetPatterns,
      dryRun,
      force,
      results,
      summary: this.generateSummary(results),
      status: 'completed',
    };
  }

  /**
   * Verify clean tool availability
   * @param context - Verification context
   * @returns Verification results
   */
  private verifyClean(context: Record<string, unknown>): unknown {
    const platform = (context.platform as string) || process.platform;

    const spec = this.spec;
    if (!spec.verify?.[platform]) {
      throw new Error(`Clean verification not supported on platform: ${platform}`);
    }

    // Basic verification - check if rm command exists
    try {
      const testPath = resolve(process.cwd(), 'test-clean-target');
      rmSync(testPath, { recursive: true, force: true });
      this.verified = true;

      return {
        platform,
        command: spec.verify[platform],
        capabilities: ['remove', 'recursive', 'force'],
        status: 'verified',
      };
    } catch (error) {
      return {
        platform,
        command: spec.verify[platform],
        error: (error as Error).message,
        status: 'failed',
      };
    }
  }

  /**
   * Get clean help information
   * @param context - Help context
   * @returns Help information
   */
  private getCleanHelp(context: Record<string, unknown>): unknown {
    const platform = (context.platform as string) || process.platform;

    const spec = this.spec;
    if (!spec.help?.[platform]) {
      throw new Error(`Clean help not available on platform: ${platform}`);
    }

    return {
      platform,
      command: spec.help[platform],
      description: 'Cleanup utility for generated code and dependencies',
      availableTargets: [
        'node_modules - Node.js dependencies',
        'dist - Distribution/build output',
        'build - Build artifacts',
        'out - Output directories',
        '.cache - Cache directories',
        '.next - Next.js cache',
        'coverage - Test coverage reports',
      ],
      options: {
        '--dry-run': 'Preview what would be cleaned',
        '--force': 'Skip confirmation prompts',
        '--targets': 'Specify custom targets (comma-separated)',
      },
      status: 'help_provided',
    };
  }

  /**
   * Clean a specific target pattern
   * @param pattern - Target pattern to clean
   * @param dryRun - Whether to perform dry run
   * @param force - Whether to force removal
   * @returns Whether target was removed
   */
  private cleanTarget(pattern: string, dryRun: boolean, force: boolean): boolean {
    let removed = false;

    // Handle different patterns
    switch (pattern) {
      case 'node_modules':
        removed = this.removeDirectoriesByName('.', 'node_modules', dryRun, force);
        break;
      case 'dist':
      case 'build':
      case 'out':
        removed = this.removeDirectoriesByName('.', pattern, dryRun, force);
        break;
      case '.cache':
      case '.next':
      case 'coverage':
        removed = this.removeDirectoriesByName('.', pattern, dryRun, force);
        break;
      default:
        // Handle custom patterns
        if (existsSync(pattern)) {
          if (dryRun) {
            console.log(`Would remove: ${pattern}`);
            removed = true;
          } else {
            rmSync(pattern, { recursive: true, force });
            removed = true;
          }
        }
        break;
    }

    return removed;
  }

  /**
   * Remove directories by name recursively
   * @param rootDir - Root directory to search
   * @param dirName - Directory name to remove
   * @param dryRun - Whether to perform dry run
   * @param force - Whether to force removal
   * @returns Whether any directories were removed
   */
  private removeDirectoriesByName(
    rootDir: string,
    dirName: string,
    dryRun: boolean,
    force: boolean
  ): boolean {
    let removed = false;

    function scanAndRemove(dir: string): void {
      try {
        const items = readdirSync(dir);

        for (const item of items) {
          const fullPath = join(dir, item);
          const stat = statSync(fullPath);

          if (stat.isDirectory()) {
            if (item === dirName) {
              if (dryRun) {
                console.log(`Would remove: ${fullPath}`);
              } else {
                rmSync(fullPath, { recursive: true, force });
              }
              removed = true;
            } else {
              // Recursively scan subdirectories (but avoid node_modules for performance)
              if (item !== 'node_modules' || dirName === 'node_modules') {
                scanAndRemove(fullPath);
              }
            }
          }
        }
      } catch (error) {
        // Ignore permission errors, etc.
      }
    }

    scanAndRemove(rootDir);
    return removed;
  }

  /**
   * Generate cleanup summary
   * @param results - Cleanup results
   * @returns Summary string
   */
  private generateSummary(
    results: Array<{ target: string; removed: boolean; error?: string }>
  ): string {
    const successful = results.filter((r) => r.removed).length;
    const failed = results.filter((r) => !r.removed).length;
    const errors = results.filter((r) => r.error).length;

    let summary = `Cleaned ${successful} targets`;
    if (failed > 0) {
      summary += `, ${failed} not found`;
    }
    if (errors > 0) {
      summary += `, ${errors} errors`;
    }

    return summary;
  }
}

