import type { IPluginConfig } from '../../../../core/interfaces/index';

describe('GitHooksPlugin', () => {
  let plugin: GitHooksPlugin;

  beforeEach(() => {
    plugin = new GitHooksPlugin({
      name: 'git-hooks-plugin',
      description: 'Git hooks management plugin',
      version: '1.0.0',
      author: 'Test Author',
      category: 'tools',
      capabilities: ['git-hooks', 'automation'],
    } as IPluginConfig);
  });

  describe('constructor', () => {
    it('should create plugin instance with config', () => {
      expect(plugin).toBeDefined();
      expect(plugin).toBeInstanceOf(GitHooksPlugin);
    });
  });

  describe('execute', () => {
    it('should default to list operation', async () => {
      const result = await plugin.execute({});
      expect(result).toHaveProperty('operation', 'list');
      expect(result).toHaveProperty('installed');
      expect(result).toHaveProperty('available');
      expect(result).toHaveProperty('templates');
    });

    it('should handle setup operation', async () => {
      const result = await plugin.execute({ operation: 'setup' });
      expect(result).toHaveProperty('operation', 'setup');
      expect(result).toHaveProperty('hooks');
      expect(result).toHaveProperty('total');
      expect(result).toHaveProperty('successful');
      expect(result).toHaveProperty('failed');
    });

    it('should handle verify operation', async () => {
      const result = await plugin.execute({ operation: 'verify' });
      expect(result).toHaveProperty('operation', 'verify');
      expect(result).toHaveProperty('hooks');
      expect(result).toHaveProperty('total');
      expect(result).toHaveProperty('verified');
      expect(result).toHaveProperty('issues');
      expect(result).toHaveProperty('status');
    });

    it('should throw error for unknown operation', async () => {
      await expect(plugin.execute({ operation: 'unknown' })).rejects.toThrow(
        'Unknown Git hooks operation: unknown'
      );
    });
  });

  describe('hook management', () => {
    it('should handle create operation', async () => {
      await expect(
        plugin.execute({
          operation: 'create',
          hook: 'test-hook',
        })
      ).rejects.toThrow('Template not found: test-hook');
    });

    it('should handle remove operation', async () => {
      const result = await plugin.execute({
        operation: 'remove',
        hooks: ['nonexistent-hook'],
      });
      expect(result).toHaveProperty('operation', 'remove');
      expect(result).toHaveProperty('hooks');
      expect(result).toHaveProperty('total', 1);
      expect(result).toHaveProperty('removed', 0);
      expect(result).toHaveProperty('failed', 0);
    });
  });

  describe('template content', () => {
    it('should return pre-commit template', () => {
      const content = (plugin as any).getTemplateContent('templates/pre-commit.sh', {});
      expect(content).toContain('#!/bin/sh');
      expect(content).toContain('Pre-commit hook template');
      expect(content).toContain('bun run lint.ts');
    });

    it('should return commit-msg template', () => {
      const content = (plugin as any).getTemplateContent('templates/commit-msg.sh', {});
      expect(content).toContain('#!/bin/sh');
      expect(content).toContain('Commit message validation hook template');
      expect(content).toContain('conventional commit format');
    });

    it('should return pre-push template', () => {
      const content = (plugin as any).getTemplateContent('templates/pre-push.sh', {});
      expect(content).toContain('#!/bin/sh');
      expect(content).toContain('Pre-push hook template');
      expect(content).toContain('bun test');
    });

    it('should return generic template for unknown path', () => {
      const content = (plugin as any).getTemplateContent('unknown-template.sh', {});
      expect(content).toContain('#!/bin/sh');
      expect(content).toContain('Generic hook template');
    });
  });

  describe('utility methods', () => {
    it('should detect package manager for linux', () => {
      const pm = (plugin as any).detectPackageManager('linux');
      expect(pm).toBe('apt');
    });

    it('should detect package manager for darwin', () => {
      const pm = (plugin as any).detectPackageManager('darwin');
      expect(pm).toBe('brew');
    });

    it('should detect package manager for win32', () => {
      const pm = (plugin as any).detectPackageManager('win32');
      expect(pm).toBe('choco');
    });

    it('should default to apt for unknown platform', () => {
      const pm = (plugin as any).detectPackageManager('unknown');
      expect(pm).toBe('apt');
    });
  });
});

/**
 * Git Hooks Management Plugin - AGENTS.md compliant TypeScript implementation
 * Single responsibility: Centralized Git hooks management and deployment
 */

import type { IPluginConfig, IRegistryManager } from '../../../../core/interfaces/index';
import { BasePlugin } from '../../../../core/base-plugin';
import { execSync } from 'child_process';
import * as fs from 'fs';
import * as path from 'path';

/**
 *
 */
interface HookTemplate {
  description: string;
  template: string;
  executable: boolean;
}

/**
 *
 */
export class GitHooksPlugin extends BasePlugin {
  private readonly hooksPath = '.git/hooks';
  private readonly templatesPath = 'templates.json';
  private installed = false;
  private readonly verified = false;
  private templates: Record<string, string[]> = {};

  /**
   * Constructor with single config parameter
   * @param config - Plugin configuration
   */
  constructor(config: IPluginConfig) {
    super(config);
    this.loadTemplates();
  }

  /**
   * Register Git hooks tool with registries
   * @param registryManager - Registry manager instance
   */
  public override async register(registryManager: IRegistryManager): Promise<void> {
    await super.register(registryManager);
    registryManager.register('DevWorkflowRegistry', 'tool.dev.git-hooks', this);
  }

  /**
   * Execute Git hooks operations (single business method)
   * @param context - Execution context
   * @returns Operation results
   */
  public override async execute(context: Record<string, unknown>): Promise<unknown> {
    const operation = (context.operation as string) || 'list';

    switch (operation) {
      case 'install':
        return this.installGit(context);
      case 'setup':
        return this.setupHooks(context);
      case 'verify':
        return this.verifyHooks(context);
      case 'list':
        return this.listHooks(context);
      case 'create':
        return this.createHook(context);
      case 'remove':
        return this.removeHook(context);
      default:
        throw new Error(`Unknown Git hooks operation: ${operation}`);
    }
  }

  /**
   * Install Git tool (dependency for hooks)
   * @param context - Installation context
   * @returns Installation results
   */
  private installGit(context: Record<string, unknown>): unknown {
    const platform = (context.platform as string) || process.platform;
    const packageManager =
      (context.packageManager as string) || this.detectPackageManager(platform);

    const spec = this.spec;
    if (!spec.install?.[platform]) {
      throw new Error(`Git installation not supported on platform: ${platform}`);
    }

    const installCommands = spec.install[platform][packageManager];
    if (!installCommands) {
      throw new Error(`Package manager ${packageManager} not supported for Git on ${platform}`);
    }

    const command = installCommands.join(' ');
    execSync(command, { stdio: 'inherit' });

    this.installed = true;
    return {
      platform,
      packageManager,
      command,
      status: 'installed',
    };
  }

  /**
   * Setup hooks from templates
   * @param context - Setup context
   * @returns Setup results
   */
  private setupHooks(context: Record<string, unknown>): unknown {
    const hooksToSetup = (context.hooks as string[]) || Object.keys(this.getHookTemplates());
    const results: Record<string, unknown>[] = [];

    this.ensureHooksDirectory();

    for (const hookName of hooksToSetup) {
      try {
        const result = this.installHook(hookName, context as Record<string, unknown>);
        results.push(result);
      } catch (error) {
        results.push({
          hook: hookName,
          status: 'failed',
          error: (error as Error).message,
        });
      }
    }

    return {
      operation: 'setup',
      hooks: results,
      total: results.length,
      successful: results.filter((r) => r.status === 'installed').length,
      failed: results.filter((r) => r.status === 'failed').length,
    };
  }

  /**
   * Verify hooks installation
   * @param context - Verification context
   * @returns Verification results
   */
  private verifyHooks(context: Record<string, unknown>): unknown {
    const hooksToVerify = (context.hooks as string[]) || Object.keys(this.getHookTemplates());
    const results: Record<string, unknown>[] = [];

    for (const hookName of hooksToVerify) {
      const hookPath = path.join(this.hooksPath, hookName);
      const exists = fs.existsSync(hookPath);
      const executable = exists ? this.isExecutable(hookPath) : false;

      results.push({
        hook: hookName,
        path: hookPath,
        exists,
        executable,
        status: exists && executable ? 'verified' : exists ? 'not_executable' : 'missing',
      });
    }

    const verifiedCount = results.filter((r) => r.status === 'verified').length;

    return {
      operation: 'verify',
      hooks: results,
      total: results.length,
      verified: verifiedCount,
      issues: results.length - verifiedCount,
      status: verifiedCount === results.length ? 'all_verified' : 'issues_found',
    };
  }

  /**
   * List available hooks and templates
   * @param context - List context
   * @returns List results
   */
  private listHooks(context: Record<string, unknown>): unknown {
    const templates = this.getHookTemplates();
    const installed: Record<string, unknown>[] = [];
    const available: Record<string, unknown>[] = [];

    // Check installed hooks
    for (const hookName of Object.keys(templates)) {
      const hookPath = path.join(this.hooksPath, hookName);
      const exists = fs.existsSync(hookPath);
      const executable = exists ? this.isExecutable(hookPath) : false;

      if (exists) {
        installed.push({
          name: hookName,
          path: hookPath,
          executable,
          template: templates[hookName],
        });
      } else {
        available.push({
          name: hookName,
          template: templates[hookName],
        });
      }
    }

    return {
      operation: 'list',
      installed: {
        count: installed.length,
        hooks: installed,
      },
      available: {
        count: available.length,
        hooks: available,
      },
      templates: Object.keys(templates).length,
    };
  }

  /**
   * Create a new hook from template
   * @param context - Create context
   * @returns Create results
   */
  private createHook(context: Record<string, unknown>): unknown {
    const hookName = context.hook as string;
    const templateName = (context.template as string) || hookName;

    if (!hookName) {
      throw new Error('Hook name is required');
    }

    return this.installHook(hookName, { template: templateName, ...context });
  }

  /**
   * Remove installed hooks
   * @param context - Remove context
   * @returns Remove results
   */
  private removeHook(context: Record<string, unknown>): unknown {
    const hooksToRemove = (context.hooks as string[]) || [];
    const results: Record<string, unknown>[] = [];

    for (const hookName of hooksToRemove) {
      try {
        const hookPath = path.join(this.hooksPath, hookName);
        const backupPath = `${hookPath}.backup`;

        if (fs.existsSync(hookPath)) {
          // Create backup
          fs.copyFileSync(hookPath, backupPath);
          fs.unlinkSync(hookPath);

          results.push({
            hook: hookName,
            status: 'removed',
            backup: backupPath,
          });
        } else {
          results.push({
            hook: hookName,
            status: 'not_found',
          });
        }
      } catch (error) {
        results.push({
          hook: hookName,
          status: 'failed',
          error: (error as Error).message,
        });
      }
    }

    return {
      operation: 'remove',
      hooks: results,
      total: results.length,
      removed: results.filter((r) => r.status === 'removed').length,
      failed: results.filter((r) => r.status === 'failed').length,
    };
  }

  /**
   * Install a specific hook from template
   * @param hookName - Name of the hook
   * @param context - Installation context
   * @returns Installation result
   */
  private installHook(hookName: string, context: Record<string, unknown>): unknown {
    const templates = this.getHookTemplates();
    const templateName = (context.template as string) || hookName;
    const template = templates[templateName];

    if (!template) {
      throw new Error(`Template not found: ${templateName}`);
    }

    const hookPath = path.join(this.hooksPath, hookName);

    // Read template content
    const templateContent = this.getTemplateContent(template.template, context);

    // Write hook file
    fs.writeFileSync(hookPath, templateContent, { mode: 0o755 });

    if (template.executable) {
      fs.chmodSync(hookPath, 0o755);
    }

    return {
      hook: hookName,
      template: templateName,
      path: hookPath,
      executable: template.executable,
      status: 'installed',
    };
  }

  /**
   * Load templates from JSON file
   */
  private loadTemplates(): void {
    try {
      // In test environment, use the current directory
      const pluginDir =
        this.config.entry_point && typeof this.config.entry_point === 'string'
          ? path.dirname(this.config.entry_point.replace('src/git-hooks-plugin.js', ''))
          : __dirname.replace('/src', '');

      const templatesFile = path.join(pluginDir, this.templatesPath);

      if (fs.existsSync(templatesFile)) {
        const templatesData = fs.readFileSync(templatesFile, 'utf8');
        this.templates = JSON.parse(templatesData);
      } else {
        // Fallback: try relative to current file
        const fallbackPath = path.join(__dirname, '..', this.templatesPath);
        if (fs.existsSync(fallbackPath)) {
          const templatesData = fs.readFileSync(fallbackPath, 'utf8');
          this.templates = JSON.parse(templatesData);
        }
      }
    } catch (error) {
      console.warn('Failed to load templates:', error);
    }
  }

  /**
   * Get hook templates from spec
   * @returns Hook templates
   */
  private getHookTemplates(): Record<string, HookTemplate> {
    const spec = this.spec as any;
    return spec.hookTemplates || {};
  }

  /**
   * Get template content with variable substitution
   * @param templateName - Name of the template (e.g., 'pre-commit')
   * @param context - Context for variable substitution
   * @returns Template content
   */
  private getTemplateContent(templateName: string, context: Record<string, unknown>): string {
    // Extract template name from path if it's a full path
    const baseName = templateName.replace('templates/', '').replace('.sh', '');

    const templateLines = this.templates[baseName];
    if (templateLines && Array.isArray(templateLines)) {
      return templateLines.join('\n');
    }

    // Fallback for unknown templates
    return `#!/bin/sh
# Generic hook template for ${baseName}

echo "Hook executed: ${baseName}"
`;
  }

  /**
   * Ensure hooks directory exists
   */
  private ensureHooksDirectory(): void {
    if (!fs.existsSync(this.hooksPath)) {
      fs.mkdirSync(this.hooksPath, { recursive: true });
    }
  }

  /**
   * Check if file is executable
   * @param filePath - Path to file
   * @returns True if executable
   */
  private isExecutable(filePath: string): boolean {
    try {
      const stats = fs.statSync(filePath);
      return !!(stats.mode & parseInt('111', 8));
    } catch {
      return false;
    }
  }

  /**
   * Detect appropriate package manager
   * @param platform - Target platform
   * @returns Package manager name
   */
  private detectPackageManager(platform: string): string {
    switch (platform) {
      case 'linux':
        return 'apt';
      case 'darwin':
        return 'brew';
      case 'win32':
        return 'choco';
      default:
        return 'apt';
    }
  }
}

/**
 * Git Tool Plugin - AGENTS.md compliant TypeScript implementation
 * Single responsibility: Git tool management with strict OO constraints
 */

import type { IPluginConfig, IRegistryManager } from '../../../../core/interfaces/index';
import { BasePlugin } from '../../../../core/base-plugin';
import { execSync } from 'child_process';

/**
 *
 */
export class GitPlugin extends BasePlugin {
  private installed = false;
  private verified = false;

  /**
   * Constructor with single config parameter
   * @param config - Plugin configuration
   */
  constructor(config: IPluginConfig) {
    super(config);
  }

  /**
   * Register Git tool with registries
   * @param registryManager - Registry manager instance
   */
  public override async register(registryManager: IRegistryManager): Promise<void> {
    await super.register(registryManager);
    registryManager.register('DevWorkflowRegistry', 'tool.dev.git', this);
  }

  /**
   * Execute Git operations (single business method)
   * @param context - Execution context
   * @returns Operation results
   */
  public override async execute(context: Record<string, unknown>): Promise<unknown> {
    const operation = (context.operation as string) || 'verify';

    switch (operation) {
      case 'install':
        return this.installGit(context);
      case 'verify':
        return this.verifyGit(context);
      case 'execute':
        return this.executeGitCommand(context);
      case 'help':
        return this.getGitHelp(context);
      default:
        throw new Error(`Unknown Git operation: ${operation}`);
    }
  }

  /**
   * Install Git tool
   * @param context - Installation context
   * @returns Installation results
   */
  private installGit(context: Record<string, unknown>): unknown {
    const platform = (context.platform as string) || process.platform;
    const packageManager =
      (context.packageManager as string) || this.detectPackageManager(platform);

    const spec = this.spec;
    if (!spec.install?.[platform]) {
      throw new Error(`Git installation not supported on platform: ${platform}`);
    }

    const installCommands = spec.install[platform][packageManager];
    if (!installCommands) {
      throw new Error(`Package manager ${packageManager} not supported for Git on ${platform}`);
    }

    const command = installCommands.join(' ');
    execSync(command, { stdio: 'inherit' });

    this.installed = true;
    return {
      platform,
      packageManager,
      command,
      status: 'installed',
    };
  }

  /**
   * Verify Git installation
   * @param context - Verification context
   * @returns Verification results
   */
  private verifyGit(context: Record<string, unknown>): unknown {
    const platform = (context.platform as string) || process.platform;

    const spec = this.spec;
    if (!spec.verify?.[platform]) {
      throw new Error(`Git verification not supported on platform: ${platform}`);
    }

    const verifyCommand = spec.verify[platform];
    const output = execSync(verifyCommand.join(' '), { encoding: 'utf8' });

    this.verified = true;
    return {
      platform,
      command: verifyCommand,
      output: output.trim(),
      version: this.parseVersion(output),
      status: 'verified',
    };
  }

  /**
   * Execute Git command
   * @param context - Command context
   * @returns Command results
   */
  private executeGitCommand(context: Record<string, unknown>): unknown {
    const { command, args = [], cwd } = context;

    if (!command) {
      throw new Error('Git command not specified');
    }

    const fullCommand = ['git', command as string, ...(args as string[])];
    const execOptions: Record<string, unknown> = {
      encoding: 'utf8',
      maxBuffer: 1024 * 1024 * 10, // 10MB buffer
    };

    if (cwd) {
      execOptions.cwd = cwd;
    }

    try {
      const output = execSync(fullCommand.join(' '), {
        encoding: 'utf8',
        maxBuffer: 1024 * 1024 * 10,
        cwd: cwd as string,
      });
      return {
        command: fullCommand,
        output: output.trim(),
        cwd: cwd || process.cwd(),
        status: 'executed',
      };
    } catch (error) {
      const execError = error as { message: string; status?: number };
      return {
        command: fullCommand,
        error: execError.message,
        exitCode: execError.status,
        cwd: cwd || process.cwd(),
        status: 'failed',
      };
    }
  }

  /**
   * Get Git help information
   * @param context - Help context
   * @returns Help information
   */
  private getGitHelp(context: Record<string, unknown>): unknown {
    const platform = (context.platform as string) || process.platform;

    const spec = this.spec;
    if (!spec.help?.[platform]) {
      throw new Error(`Git help not available on platform: ${platform}`);
    }

    const helpCommand = spec.help[platform];
    const output = execSync(helpCommand.join(' '), { encoding: 'utf8' });

    return {
      platform,
      command: helpCommand,
      output: output.trim(),
      status: 'help_provided',
    };
  }

  /**
   * Detect appropriate package manager
   * @param platform - Target platform
   * @returns Package manager name
   */
  private detectPackageManager(platform: string): string {
    switch (platform) {
      case 'linux':
        return 'apt';
      case 'darwin':
        return 'brew';
      case 'win32':
        return 'choco';
      default:
        return 'apt';
    }
  }

  /**
   * Parse Git version from output
   * @param output - Version command output
   * @returns Parsed version
   */
  private parseVersion(output: string): string {
    const match = /git version (\d+\.\d+\.\d+)/.exec(output);
    return match ? match[1] : 'unknown';
  }
}

